import React, { Component } from 'react';
import './ws.css'
//import './websocket.js'




class Websocket extends Component {

    constructor(props) {
      super(props);
      this.state = { apiResponse: "" };
    }

    callAPI() {
        fetch("http://104.128.95.54:9000/testAPI")
            .then(res => res.text())
            .then(res => this.setState({ apiResponse: res }))
            .then(err => err);
    }

    componentDidMount() {
        this.callAPI();
    }

    handleClick() {
      <>
        const msgBox = document.getElementById('msg-need-send');
        ws.send(msgBox.value);
      </>
      console.log(msgBox); 
  } 




    render(){
        return (


          <div className="websocket">
          <div className="receive">
            <p>服务端返回的消息</p>
            <div id="receive-box" />
          </div>
          <div className="send">
            <textarea type="text" id="msg-need-send" defaultValue={"666"} />
            <p>
              <button id="send-btn" onClick={this.handleClick}>点击发消息给服务端</button>
            </p>
          </div>
          <button id="exit">关闭连接</button>
        </div>


        );
      }
    }


export default Websocket
