import React, { Component } from 'react';
import './ws.css'
//import './websocket.js'

const receiveBox = document.getElementById('receive-box')
const msgBox = document.getElementById('msg-need-send')

const ws = new WebSocket('ws://104.128.95.54:3030/websockets/test')
ws.onopen = e => {
  console.log('WebSocket 连接状态： ${ws.readyState}')
}

ws.onmessage = data => {
/*   receiveBox.innerHTML += '<p>${data.data}</p>'
  receiveBox.scrollTo({
    top: receiveBox.scrollHeight,
    behavior: "smooth"
  }) */
  console.log('receive')
}

ws.onclose = data => {
  console.log('WebSocket连接已关闭')
  console.log(data);
}

function handleClick(e) {
  //ws.send(msgBox.value)
  e.preventDefault();
  console.log('链接被点击')
}

class Websocket extends Component {

    constructor(props) {
      super(props);
      this.state = { apiResponse: "" };
    }

    callAPI() {
        fetch("http://104.128.95.54:9000/testAPI")
            .then(res => res.text())
            .then(res => this.setState({ apiResponse: res }))
            .then(err => err);
    }

    componentDidMount() {
        this.callAPI();
    }



    render(){
        return (


          <div className="websocket">
          <div className="receive">
            <p>服务端返回的消息</p>
            <div id="receive-box" />
          </div>
          <div className="send">
            <textarea type="text" id="msg-need-send" defaultValue={""} />
            <p>
              <button id="send-btn" onClick={this.handleClick}>点击发消息给服务端</button>
            </p>
          </div>
          <button id="exit">关闭连接</button>
        </div>


        );
      }
    }


export default Websocket
